import React, {Component} from 'react';
import { Link, Router } from 'react-router-dom';
import {Switch} from 'react-router-dom';
import {Route} from 'react-router-dom';
class Product extends Component {
    state={
    product:1,  
};
componentDidMount(){
const data = {

"bicycle":{id:1,name:"Bicycle",price:30,quantity:15,desc:"Bicycle is Good"},
"TV":{id:2,name:"TV",price:40,quantity:16,desc:"TV is Good"},
"pencil":{id:3,name:"pencil",price:50,quantity:17,desc:"Pencil is Good"},

}

for(let key in data){
    if(data[key].id == this.props.matcg.params.id){
        this.setState({
            product: data[key]
        });
        break;
    }
}
}

 render(){
return ( <div>
         <p>{this.props.match.params.id}</p>
         <p>name:{this.state.product.name}</p>
         <p>id:{this.state.id}</p>
         <p>price:{this.state.price}</p>
         <p>quantity:{this.state.quantity}</p>
         <p>desc:{this.state.desc}</p>
        </div>

);

 }

}


export default Product;
