import React, {Component} from 'react';
import { Link, Router } from 'react-router-dom';
import {Switch} from 'react-router-dom';
import {Route} from 'react-router-dom';
class Products extends Component {
state = {
 products : [
  {id:1,name:"Bicycle",price:30,quantity:15,desc:"Bicycle is Good"},
  {id:2,name:"TV",price:40,quantity:16,desc:"TV is Good"},
  {id:3,name:"pencil",price:50,quantity:17,desc:"Pencil is Good"},

 ],

};
 render(){
return ( <div>

            {this.state.products.map((product) => (
            <Link to={`/product/${product.id}`}>{product.name}</Link>
            ))}
          <Switch>
        <Route exact path="/product/1" component={Profile}/>
        <Route exact path="/product/2" component={Profile}/>
        <Route exact path="/product/3" component={Profile}/>


          </Switch>

        </div>

);

 }

}

export default Products;
